  (print
    "Complete:     " complete ",  %" (int (* 100 (/ complete total)))\newline
    "Didn't start: " zero ",  %" (int (* 100 (/ zero total)))\newline
    "In Progress:  " progress ",  %" (int (* 100 (/ progress total))))
  
  
  
  (print (sel (to-dataset ($where {:Percent {= 0} :Country {= country}})) :cols [0 1 11]))
  
  (chart-view (pie-chart ["Zero" "Complete" "In-Progress"] [zero complete progress] :legend true))
  
))

