(with-data compt
(let [total (nrow ($ :Percent))
      zero (nrow ($where {:Percent 0}))
      complete (nrow ($where {:Percent 100}))
      progress(nrow ($where {:Percent {:gt 0 :lt 100}}))]

(print
    " Complete:    " complete ",  %" (* 100. (/ complete total))\newline
    "Didn't start: " zero ",  %" (* 100. (/ zero total))\newline
    "In Progress:  " progress ",  %" (* 100. (/ progress total)))

(chart-view (pie-chart ["Zero" "Complete" "In-Progress"] [zero complete progress] :title "MENA"))))
                                     
