;; **
;;; ## Country by Country
;; **

;; @@
(with-data compt
  (let [country "OMAN"
      	total (nrow ($ :Percent ($where {:Country country})))
      	zero (nrow ($where {:Percent 0 :Country country}))
      	complete (nrow ($where {:Percent 100 :Country country}))
      	progress(nrow ($where {:Percent {:gt 1 :lt 99} :Country country}))]  
    
  (print
    "Complete:     " complete ",  %" (* 100. (/ complete total))\newline
    "Didn't start: " zero ",  %" (* 100. (/ zero total))\newline
    "In Progress:  " progress ",  %" (* 100. (/ progress total)))
  
  
  
  (print (sel (to-dataset ($where {:Percent {= 0} :Country {= country}})) :cols [0 1 11]))
  
  (chart-view (pie-chart ["Zero" "In-Progress" "Complete"] [zero progress complete] :legend true))
  
))

