(with-data (to-dataset ($where {:Country "ALGERIA"} compt))
  (let [total (nrow ($ :Percent))
        zero (nrow ($where {:Percent {= 0}}))
        complete (nrow ($where {:Percent {= 100}}))
        progress(nrow ($where {:Percent {:gt 0 :lt 100}}))]
    
    (print
      " Complete:    " complete ",  %" (int (* 100 (/ complete total)))\newline
      "In Progress:  " progress ",  %" (int (* 100 (/ progress total)))\newline  
      "Didn't start: " zero ",  %" (int (* 100 (/ zero total))))

(chart-view (bar-chart ["Complete" "In-Progress" "Zero"] [complete progress zero] 
                       :title "Algeria"
                       :x-label "Progress"
                       :y-label "Numbers"))))
