;; @@
(defn data [country]
  (let
      [ex (nrow (to-dataset ($where {:Country country :Course "Introduction to Ex Protection"} trn)))
       coshh (nrow (to-dataset ($where {:Country country :Course "Introduction to COSHH"} trn)))
       elec (nrow (to-dataset ($where {:Country country :Course "Introduction to Electricity"} trn)))
       bml (nrow (to-dataset ($where {:Country country :Course "SLS Basic Mud Logging Course"} trn)))
       
       cnt (to-dataset ($where {:Country country} compt))
       total (nrow cnt)
       complete (nrow ($where {:Percent 100} cnt))
       progress (nrow ($where {:Percent{:gt 0 :lt 100}} cnt))
       zero (nrow ($where {:Percent 0} cnt))]
    
    (println country","total","bml","ex","elec","coshh","complete","progress","zero)))

(def country 
  ["SAUDI ARABIA" "KUWAIT" "OMAN" "IRAQ" "EGYPT" "UNITED ARAB EMIRATES" "YEMEN" "TURKMENISTAN" "ALGERIA"])

(pprint (map data country))

